import './App.css';
import { useState, useEffect, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import logo from './logo.jpg'; // Asegúrate de que tu logo esté en la carpeta src


function App() {
  const [activeTab, setActiveTab] = useState('externo');
  const [pedidos, setPedidos] = useState([]);
  const [mesas, setMesas] = useState([
    // { id: 1, numero: 1, estado: 'disponible', tipo: null, pedidoActual: null },
    // { id: 2, numero: 2, estado: 'disponible', tipo: null, pedidoActual: null },
    // { id: 3, numero: 3, estado: 'disponible', tipo: null, pedidoActual: null },
    // { id: 4, numero: 4, estado: 'disponible', tipo: null, pedidoActual: null },
  ]);
  const [productos, setProductos] = useState([]);

  // En producción, la API estará en la misma URL, bajo la ruta /api
  // En desarrollo, podemos usar un proxy o mantener la URL completa.
  // En producción, la API estará en la misma URL, bajo la ruta /api
// En desarrollo, podemos usar un proxy o mantener la URL completa.
const API_URL = process.env.NODE_ENV === 'development' 
  ? 'http://localhost:3001/api'  // Si pruebas localmente con un servidor
  : '/api';  // En producción en Vercel

  const fetchData = useCallback(async () => {
  try {
    console.log("Fetching data from:", API_URL);

    const [productosRes, mesasRes, pedidosRes] = await Promise.all([
      fetch(`${API_URL}/productos`),
      fetch(`${API_URL}/mesas`),
      fetch(`${API_URL}/pedidos`),
    ]);

    // Verificar si las respuestas son exitosas
    if (!productosRes.ok) {
      console.error("Error productos:", productosRes.status, productosRes.statusText);
      throw new Error(`Productos: ${productosRes.status}`);
    }
    if (!mesasRes.ok) {
      console.error("Error mesas:", mesasRes.status, mesasRes.statusText);
      throw new Error(`Mesas: ${mesasRes.status}`);
    }
    if (!pedidosRes.ok) {
      console.error("Error pedidos:", pedidosRes.status, pedidosRes.statusText);
      throw new Error(`Pedidos: ${pedidosRes.status}`);
    }

    const productosData = await productosRes.json();
    const mesasData = await mesasRes.json();
    const pedidosData = await pedidosRes.json();

    console.log("Datos recibidos:", {
      productos: productosData.data?.length || 0,
      mesas: mesasData.data?.length || 0,
      pedidos: pedidosData.data?.length || 0
    });

    setProductos(productosData.data || []);
    setMesas(mesasData.data || []);
    setPedidos(pedidosData.data || []);

  } catch (error) {
    console.error("Error fetching data:", error);
    // Inicializar con arrays vacíos para evitar errores
    setProductos([]);
    setMesas([]);
    setPedidos([]);

    // Mostrar alerta solo en desarrollo
    if (process.env.NODE_ENV === 'development') {
      alert(`Error cargando datos: ${error.message}. Revisa la consola para más detalles.`);
    }
  }
}, [API_URL]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  return (
    <div className="min-h-screen bg-gray-50 text-gray-900 flex flex-col">
      <Header activeTab={activeTab} setActiveTab={setActiveTab} />

      <main className="flex-1 pt-20 pb-24 px-4 overflow-auto">
        <AnimatePresence mode="wait">
          {activeTab === 'externo' && (
            <motion.div
              key="externo"
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: -20 }}
              transition={{ duration: 0.2 }}
            >
              <PedidoExterno productos={productos} onPedidoConfirmado={fetchData} />
            </motion.div>
          )}

          {activeTab === 'interno' && (
            <motion.div
              key="interno"
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: -20 }}
              transition={{ duration: 0.2 }}
            >
              <PedidoInterno productos={productos} mesas={mesas} setMesas={setMesas} onPedidoFinalizado={fetchData} />
            </motion.div>
          )}

          {activeTab === 'admin' && (
            <motion.div
              key="admin"
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: -20 }}
              transition={{ duration: 0.2 }}
            >
              <AdminPanel productos={productos} setProductos={setProductos} pedidos={pedidos} mesas={mesas} onDataChange={fetchData} />
            </motion.div>
          )}
        </AnimatePresence>
      </main>

      <TabBar activeTab={activeTab} setActiveTab={setActiveTab} />
    </div>
  );
};

